import React from "react";
//import './App.css';

const todos = [
  { text: 'Cortar cebolla', completed: false },
  { text: 'Tomar el curso de react', completed: false },
  { text: 'Tercera tarea', completed: false },
]

function App(props) {
  return (

    <React.Fragment>

      <TodoCounter />
      <h2>Has completado 2 de 3 ToDos</h2>

      <TodoSearch />
      <input placeholder="Cebolla" />

      <TodoList>
        {todos.map(todo => (
          <TodoItem />
        ))}
      </TodoList>

      <CreateTodoButton />
      <button>+</button>

    </React.Fragment>

  );
}

export default App;
